  withCredentials: true
})  


function PostsPage() {

  return (
    <div className = "">
      <header> 
        <p> header </p>
      </header>
      <div className="site-body">
        <div className="posts">
        <NewPhotoPhoto currentUser = "davey" groupID = { 70 } api = { axiosRequest }/>
        <PostList groupID = { 70 } api = { axiosRequest }/>
        </div>

      </div>
    </div>
  );
}

export default PostsPage;


