import React, { useState, useEffect, useContext } from 'react';
import { useQuery } from "react-query";
import { Link } from 'react-router-dom';
//import  functions from "../../functions/functions";
//import useHello from '../../functions/hooks/useHello';

async function getGroupUsers(groupID, api) {

  //const groupURL = "http://localhost:3003/groups/user/" + currentUser; 
  const groupUsersURL = "http://localhost:3003/group/users/" + groupID; 
  const { data } = await api.get(groupUsersURL)

  return data
} 


const GroupUsers = (props) => {
    const groupID = props.groupID;
    const currentUser = props.currentUser;
    const api = props.api;

    const { isLoading, data, isError, error  } = useQuery(['group-users-', groupID], () => getGroupUsers(groupID, api), 
        { refetchInterval: 10000000 }
    )

    var activeGroupUsers = data.activeGroupUsers;
    var pendingGroupUsers = data.pendingGroupUsers;

    return (
        <div className="groups">
            { isLoading && <div> loading... </div>}
            { isError && <div> There was an error fetching the posts { error.message } </div>}
            { data && console.log(activeGroupUsers)}
            { data && console.log(pendingGroupUsers)}
            {data && activeGroupUsers.map(user => (
                <p> {user } </p>
            ))}
        </div>
    );
}

export default GroupUsers;


