import React, { useState } from 'react'
import useLocalStorage from '../../hooks/useLocalStorage';
import IndividualGroupUser from './IndividualGroupUser';

const SimpleAddGroupUsers= ({ currentUser, api }) => {
  const [newGroupUser, setNewGroupUser] = useLocalStorage('newGroupUser', '')
  let david = {
    userID: 1,
    userName: "david",
  }
  let sam = {
    userID: 2,
    userName: "sam",
  }
  let frodo = {
    userID: 3,
    userName: "frodo",
  }

  let usersArray = [david, sam, frodo]

  const [users, addUser] = useState(usersArray)

  //Add User to Group
  const addNewGroupUser = (userID) => {
    console.log("You are going to add " + userID)
    setNewGroupUser(userID)
    const newUsers = users.filter(user => user.userID !== userID);
    addUser(newUsers)
  }

  //Remove User from Group
  const removeNewGroupUser = (userID) => {
    console.log("You will remove " + userID)

  }

  return (
      <div> 
          {users.map((user) => (
              <IndividualGroupUser key = { user.userID } addNewGroupUser = {addNewGroupUser} user = {user} />
          ))} 
      </div>
  );
}


export default SimpleAddGroupUsers;




