import { useState, useEffect } from 'react';

/*

//FUNCTIONS A: Login Functions 
//Function A.1: Login a User 
//Function A.2: Logout a User 
//Function A.3: Get Login Status 
*/
export function sayHello(userName) {
    console.log("hello " + userName)
}

//Function A.1: Filter Array based on zipcode and event type
export function loginStatus(userName) {
    let userStatus = {
        isAuthenticated: false,
        user: userName,
    };

    if(userName == 'null') {
        userStatus.isAuthenticated = false;
      } else {
        userStatus.isAuthenticated = true;
      }

      return userStatus;
}

export function loginUser(userName) {
    console.log("login " + userName)
}

export function logouUser(userName) {
    console.log("logout " + userName)
}


