import React, { useState } from 'react'


const IndividualGroupUser = ({addNewGroupUser, removeNewGroupUser, user }) => {
  let userID = user.userID
  let userName = user.userName

  return (
      <div> 
        <div className='simple-border'> 
          <p> {userID } | {userName} </p>
          <button onClick={() => addNewGroupUser(userID)}>Add Me to your Group{userName}</button>
          <button onClick={() => removeNewGroupUser(userID)}>Remove ME {userName}</button>
        </div>
      </div>
  );
}


export default IndividualGroupUser;
