import React, { useState } from 'react';
import { BrowserRouter, Route, Routes, Link } from 'react-router-dom'
//import { BrowserRouter, Link, Navigate, Route, Routes } from 'react-router-dom'
import { QueryClientProvider, QueryClient } from 'react-query';
//https://html5boilerplate.com/
import Login from './pages/LoginPage';
import Register from './pages/Register';
import Groups from './pages/Groups';
//import PrivateRoutes from './functions/utils/PrivateRoutes.js';
import { UserContext } from "./functions/context/UserContext";

//import { sayHello } from './functions/functions';

import './index.css';
import './style/style.css';
import './style/external/normalize.css';

const queryClient = new QueryClient()

function App() {
  //const [currentUser, setLoginState] = useState('null');
  useEffect(() => {
    const data = localStorage.getItem("currentUserLoggedIn");
    const currentUser = JSON.parse(data);
    setCurrentUser(currentUser)
  }, []);

  return (
    <UserContext.Provider value={{currentUser, setCurrentUser}}>
      <div className="App">
        <QueryClientProvider client={queryClient}>
          <BrowserRouter>
            <nav>
            <p> Username {currentUser} </p>
              <div className = "spacer"> <p> hi </p> <p> hi </p><p> hi </p></div>
              <Link className="loginInput" to="/login">Login </Link>
              <Link className="loginInput" to="/register">Register </Link>
              <Link className="loginInput" to="/groups"> Groups </Link>
              <Link className="loginInput" to="/users"> Users </Link>
            </nav>

            <Routes>
              <Route path = "/" element = {<Groups />} /> 
              <Route path = "/login" element = {<Login />} /> 
              <Route path = "/register" element = {<Register />} /> 
              <Route path = "/groups" element = {<Groups />} /> 
            </Routes>

          </BrowserRouter>
        </QueryClientProvider>
      </div>
    </UserContext.Provider>
  );
}

export default App;



