import React, { useState, useRef } from 'react';
import { useMutation, useQueryClient } from "react-query";
import axios from "axios";

function Example() {   
    const [items, setItems] = useState([])
    const inputRef = useRef()

    function onSubmit(e) {
      e.preventDefault();
      const value = inputRef.current.value;

      if(value == "") {
        return ;
      } else {
        setItems(prev => {
          return [...prev, value]
        })
      }  
    } 

