      <div className = "login">
        <p> Search </p>
        <input type="search" /> <br /><br />
        <form onSubmit={onSubmit}>
            <p> New Item: </p>
            <input ref = {inputRef} type="text"  />
            <button type="submit" className="loginButton" > Add </button>
        </form>
        <h4> Items </h4>
        {
          items.map(item => {
            <p> item </p>
          })
        }
      </div>
    );
  }

export default Example;
