    return (     

        <div className="post" >
            <Link to={`/friends/${friend.friendName}`}> {friend.friendName } </Link>
            <p> {friend.userName} </p>
            {friend.alsoYourFriend ? 
                <p> Already friends </p>
                : 
                <AddFriend api = { api } friend = { friend } currentUser = {currentUser}  />    
