import { Outlet, Navigate } from 'react-router-dom'

const PrivateRoutes = () => {

    if(currentUser == 'null') {
        userLoggedIn = 'false'  
      } else {
        userLoggedIn = 'true' 
      }


    return(
        userLoggedIn ? <Outlet/> : <Navigate to="/login"/>
    )
}

export default PrivateRoutes
