import React from 'react';
import { useQuery } from "react-query";
//import Comment from './display/IndividualComment';
//import NewComment from './actions/NewComment';

async function getComments(postID, api) {
    const postCommentsURL = "http://localhost:3003/comments/72"; 
    //const postCommentsURL = "http://localhost:3003/comments/" + postID; 
    const response = await api.get(postCommentsURL)

    return response.data

} 

//<Comments api = { api } groupID = { groupID } post = { post } currentUser = {currentUser}  /> 
const CommentList = ({api, groupID, postID}) => {
    const localData = localStorage.getItem("localStorageCurrentUser");
    const currentUser = JSON.parse(localData);
    console.log("Comments: (welll posts right now.. ) Getting posts for the post " + postID)

    const onError = (error) => {
      console.log("Do something here if there is Error!")
      console.log(error)
    }

    const { isLoading, data, isError, error  } = useQuery(['post-comments', postID], () => getComments(postID, api), 
      { refetchInterval: 10000000,
        onError: onError
      }
    )

    const currentPosts = data;

  return (
  <div className="posts">
       <p><b> Group Posts </b> </p>
       <div className = "post-list" >
          { isLoading && <div> loading... </div>}
          { isError && <div> There was an error fetching the posts { error.message } </div>}
          {data && currentPosts.map((post) => (
            console.log(post)
          ))}
       </div>
  </div>
  );
  }

export default CommentList;
