import React, { useState } from 'react'
import SimpleChild from './SimpleChild'


const SimpleClickParent = ({ currentUser, api }) => {
  let david = {
    userID: 1,
    userName: "david",
  }
  let sam = {
    userID: 2,
    userName: "sam",
  }
  let frodo = {
    userID: 3,
    userName: "frodo",
  }

  let usersArray = [david, sam, frodo]

  const [users, addUser] = useState(usersArray)

  return (
      <div> 
          {users.map((user) => (
            <SimpleChild user = { user } />
          ))} 
      </div>
  );
}


export default SimpleClickParent;
