function PostsPage() {

  return (
    <div id = "site-wrapper">
        <NewPost currentUser = "davey" groupID = { 70 } api = { axiosRequest }/>

        <PostList groupID = { 70 } api = { axiosRequest }/>
    </div>
  );
}

export default PostsPage;


